package package3;

public class DatatypeExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a = true;
		System.out.println("Boolean Data: "+a);
		boolean b=false;
		System.out.println("Boolean Data: "+b);
		char c='n';
		System.out.println("Char Data: "+c);
		short d=3;
		System.out.println("Short Data: "+d);
		int e=10;
		System.out.println("Int Data: "+e);
		float f=1.2f;
		System.out.println("Float Data: "+f);
		double g=3.56;
		System.out.println("Double Data: "+g);
		long h=345678L;
		System.out.println("Long Data:"+h);

	}

}
